mapper.select:
SELECT COUNT(*) > 0 FROM cdn_prefix 
WHERE name = #{name}

    AND id != #{excludeId}

SELECT COUNT(*) > 0 FROM cdn_prefix 
WHERE prefix_url = #{prefixUrl}

    AND id != #{excludeId}

mapper.delete:
DELETE FROM cdn_prefix WHERE id IN
#{id}
AND is_default = false

mapper.select:
SELECT * FROM cdn_prefix 
ORDER BY create_time DESC 
LIMIT #{limit}

mapper.update:
UPDATE cdn_prefix 
SET description = #{description}, update_time = NOW()
WHERE id = #{id}

UPDATE cdn_prefix 
SET enabled = #{enabled}, update_time = NOW()
WHERE id = #{id}
